import axios, { AxiosInstance } from 'axios';
import type { Category, CreateCategoryDto, UpdateCategoryDto, Product, CreateProductDto, UpdateProductDto, Order, CreateOrderDto, UpdateOrderDto, RevenueReportDto } from '../types/api';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

class ApiService {
  private client: AxiosInstance;

  constructor() {
    this.client = axios.create({
      baseURL: API_BASE_URL,
      headers: {
        'Content-Type': 'application/json',
      },
    });
  }

  // Categories
  async getCategories(): Promise<Category[]> {
    const response = await this.client.get<Category[]>('/categories');
    return response.data;
  }

  async getCategoryById(id: number): Promise<Category> {
    const response = await this.client.get<Category>(`/categories/${id}`);
    return response.data;
  }

  async createCategory(data: CreateCategoryDto): Promise<Category> {
    const response = await this.client.post<Category>('/categories', data);
    return response.data;
  }

  async updateCategory(id: number, data: UpdateCategoryDto): Promise<Category> {
    const response = await this.client.patch<Category>(`/categories/${id}`, data);
    return response.data;
  }

  async deleteCategory(id: number): Promise<Category> {
    const response = await this.client.delete<Category>(`/categories/${id}`);
    return response.data;
  }

  // Products
  async getProducts(categoryId?: number): Promise<Product[]> {
    const params = categoryId ? { categoryId } : {};
    const response = await this.client.get<Product[]>('/products', { params });
    return response.data;
  }

  async getProductById(id: number): Promise<Product> {
    const response = await this.client.get<Product>(`/products/${id}`);
    return response.data;
  }

  async createProduct(data: CreateProductDto): Promise<Product> {
    const response = await this.client.post<Product>('/products', data);
    return response.data;
  }

  async updateProduct(id: number, data: UpdateProductDto): Promise<Product> {
    const response = await this.client.patch<Product>(`/products/${id}`, data);
    return response.data;
  }

  async deleteProduct(id: number): Promise<Product> {
    const response = await this.client.delete<Product>(`/products/${id}`);
    return response.data;
  }

  // Orders
  async getOrders(customerName?: string): Promise<Order[]> {
    const params = customerName ? { customerName } : {};
    const response = await this.client.get<Order[]>('/orders', { params });
    return response.data;
  }

  async getOrderById(id: number): Promise<Order> {
    const response = await this.client.get<Order>(`/orders/${id}`);
    return response.data;
  }

  async createOrder(data: CreateOrderDto): Promise<Order> {
    const response = await this.client.post<Order>('/orders', data);
    return response.data;
  }

  async updateOrder(id: number, data: UpdateOrderDto): Promise<Order> {
    const response = await this.client.patch<Order>(`/orders/${id}`, data);
    return response.data;
  }

  async deleteOrder(id: number): Promise<void> {
    await this.client.delete(`/orders/${id}`);
  }

  // Revenue
  async getDailyRevenue(date?: string): Promise<RevenueReportDto> {
    const params = date ? { date } : {};
    const response = await this.client.get<RevenueReportDto>('/revenue/daily', { params });
    return response.data;
  }

  async getRevenueRange(startDate: string, endDate: string): Promise<RevenueReportDto[]> {
    const response = await this.client.get<RevenueReportDto[]>('/revenue/range', {
      params: { startDate, endDate },
    });
    return response.data;
  }
}

export const apiService = new ApiService();
export default apiService;

